mapper.delete:
DELETE FROM after_board
	WHERE b_id = #{b_id}
	AND dp_id = #{dp_id}

mapper.update:
UPDATE after_board SET b_title = #{b_title}, b_content = #{b_content}
	WHERE b_id = #{b_id} 
	AND dp_id = #{dp_id};

mapper.delete:
DELETE FROM after_board_plus
	WEHRE bplus_id = #{bplus_id}
	AND dp_id = #{dp_id}
